def max_value(arr, n):
    if n == 0:
        return -1
    elif n == 1:
        return arr[0]
    elif n == 2:
        return arr[0] + arr[1]
    else:
        max_sum = 0
        for i in range(n):
            sum = arr[(n + i) % n] + arr[(n - 1 + i) % n]
            if max_sum < sum:
                max_sum = sum
        return max_sum
t = int(input())
while t > 0:
    n = int(input())
    arr = list(map(int, input().split()))
    print(max_value(arr, n))
    t -= 1

