T = int(input())
for i in range(T):
    (X, A, B, C) = map(int, input().split())
    minCost = min(A, B, C)
    Sum = 0
    for i in range(1, X):
        Sum += minCost
    if A == minCost:
        remaining = [B, C]
    elif B == minCost:
        remaining = [A, C]
    else:
        remaining = [A, B]
    Sum += min(remaining)
    print(Sum)

class Solution:

    def solve(self, x, a, b, c):
        current = x - 1
        s = sorted([a, b, c])
        ans = current * s[0]
        ans += s[1]
        return ans

class Driver:

    def __init__(self):
        self._solver = Solution()

    def drive(self):
        test_cases = int(input().strip())
        for _ in range(test_cases):
            (x, a, b, c) = map(int, input().strip().split(' '))
            ans = self._solver.solve(x, a, b, c)
            print(ans)
Driver().drive()

