n = int(input())

def secondmin(arr):
    minimum = min(arr)
    arr.remove(minimum)
    return min(arr)
for _ in range(n):
    arr = list(map(int, input().split()))
    x = arr[0]
    m = min(arr[1:len(arr) + 1])
    sm = secondmin(arr[1:len(arr) + 1])
    if x == 2:
        print(m + sm)
    else:
        print(m * (x - 1) + sm)

for _ in range(int(input())):
    (x, a, b, c) = map(int, input().split())
    l = [a, b, c]
    s = sorted(l)
    if x > 0 and x <= 2:
        print(s[0] + s[1])
    elif x > 2:
        print(s[0] * (x - 1) + s[1])

for _ in range(int(input())):
    list1 = list(map(int, input().split()))
    maxi = max(list1[1], list1[2], list1[3])
    mini = min(list1[1], list1[2], list1[3])
    list1 = list(list1)
    list2 = [list1[1], list1[2], list1[3]]
    mini_count = list2.count(mini)
    maxi_count = list2.count(maxi)
    second_minimum = 0
    for i in list1:
        if i != mini and i != maxi:
            second_minimum = i
        else:
            pass
    l = list1[0] - 1
    condition = [list1[1] != list1[2], list1[2] != list1[3], list1[3] != list1[1]]
    if all(condition):
        print(l * mini + second_minimum)
    elif any(condition):
        if mini_count > 1:
            print(list1[0] * mini)
        else:
            print(l * mini + maxi)
    else:
        print(list1[0] * mini)
