from math import dist
for _ in range(int(input())):
    (n, x, y) = map(int, input().split())
    if n % 2:
        center = (-(-n // 2), -(-n // 2))
        print(4 * n - 4 - 2 * max(abs(x - int(center[0])), abs(y - int(center[1]))))
    else:
        center_even = [[x, y] for x in range(-(-n // 2), -(-n // 2) + 2) for y in range(-(-n // 2), -(-n // 2) + 2)]
        distance = {tuple(c): dist((x, y), c) for c in center_even}
        center = min(distance, key=distance.get)
        print(4 * n - 5 - 2 * max(abs(x - int(center[0])), abs(y - int(center[1]))))

