def cutinhalf(arr):
    n = len(arr)
    if n % 2 == 0:
        mid = n // 2
        (l1, l2) = (arr[:mid], arr[mid:])
        return (l1, l2)
    else:
        mid = n // 2 + 1
        (l1, l2) = (arr[:mid], arr[mid:])
        return (l1, l2)
