for _ in range(int(input())):
    n = int(input())
    b = list(map(int, input().split()))
    b_sum = sum(b)
    a_sum = b_sum // (n + 1)
    a = []
    if n > 1:
        for i in b:
            j = i - a_sum
            a.append(j)
    else:
        a.append(b[0] // 2)
