def make(l, N):
    ref = []
    for i in range(1, N + 1):
        ref.append(i)
        l.sort()
    for j in range(N):
        if ref[j] < l[j]:
            return -1
    for j in range(N):
        ref[j] = ref[j] - l[j]
    return sum(ref)

