def count(x):
    temp = 0
    for i in x:
        if i == '1':
            temp = temp + 1
    return temp

def ans(x, y):
    c1 = count(x)
    c2 = count(y)
    res = ''
    if c1 + c2 <= len(x):
        for _ in range(c1 + c2):
            res = res + '1'
        for _ in range(len(x) - (c1 + c2)):
            res = res + '0'
    else:
        n = 2 * len(x) - (c1 + c2)
        for _ in range(n):
            res = res + '1'
        for _ in range(len(x) - n):
            res = res + '0'
    return res
