def solve(n, lst):
    pos = lst.count(1)
    neg = lst.count(-1)
    if abs(pos - neg) < 2 or (abs(pos - neg) == 2 and pos & 1 == 0 and (neg & 1 == 0)):
        print('Yes')
    else:
        print('No')
