import math
for t in range(int(input())):
    n = int(input())
    a = [int(i) for i in input().split(' ')]
    if n % 2 != 0:
        a1 = math.floor(n / 2)
        a2 = n - a1
        if a.count(-1) == a1 and a.count(1) == a2 or (a.count(-1) == a2 and a.count(1) == a1):
            print('Yes')
        else:
            print('No')
    elif n % 4 == 0:
        if a.count(-1) == a.count(1):
            print('Yes')
        else:
            print('No')
    else:
        a1 = math.floor(n / 2) + 1
        a2 = math.floor(n / 2) - 1
        if a.count(-1) == a1 and a.count(1) == a2 or (a.count(-1) == a2 and a.count(1) == a1):
            print('Yes')
        elif a.count(-1) == a.count(1):
            print('Yes')
        else:
            print('No')

t = int(input())
while t > 0:
    t = t - 1
    n = int(input())
    a = [int(x) for x in input().split(' ')]
    cnt_a = 0
    cnt_b = 0
    for i in range(n):
        if a[i] == 1:
            cnt_a += 1
        else:
            cnt_b += 1
    mn = min(cnt_a, cnt_b)
    mx = max(cnt_a, cnt_b)
    if mx <= 2 and mn == 1:
        print('Yes')
    elif mx > 2 and mn == 1:
        print('No')
    elif mn == 0:
        print('No')
    elif n % 2 == 0 and mx % 2 == 0 and (mx - mn <= 2):
        print('Yes')
    elif n % 2 == 0 and mx % 2 != 0 and (mx == mn):
        print('Yes')
    elif n % 2 == 0 and mx % 2 != 0 and (mx != mn):
        print('No')
    elif n % 2 != 0 and mx - mn <= 1:
        print('Yes')
    else:
        print('No')

