def generate_permutation(n):
    permutation = list(range(1, n + 1))
    for i in range(1, n):
        prefix_sum = sum(permutation[:i])
        if prefix_sum % (i + 1) == 0:
            if i == n - 1:
                return [-1]
            (permutation[i], permutation[i + 1]) = (permutation[i + 1], permutation[i])
    return permutation
