T = int(input())
for i in range(T):
    a = input()
    (N, X) = [int(i) for i in a.split(' ')]
    string = ''
    if N % 2 == 0 and X % 2 == 0 or (N % 2 != 0 and X % 2 != 0):
        string += '*'
        N -= 1
    if X < 0:
        x = -X + 1
    else:
        x = X - 1
    if x > N:
        print(-1)
    else:
        A = int((x + N) / 2)
        B = int((N - x) / 2)
        if X < 0:
            string += '-' * A + '+' * B
        else:
            string += '+' * A + '-' * B
        print(string)

