T = int(input())
for i in range(T):
    (N, X) = map(int, input().split())
    if X > N + 1 or X <= -N:
        print('-1')
    else:
        if X >= 1:
            print('+' * (X - 1), end='')
            flag = 1
        else:
            print('-' * (1 - X), end='')
            flag = -1
        numbers = N - flag * (X - 1)
        if numbers % 2 == 0:
            print('+' * int(numbers / 2), end='')
            print('-' * int(numbers / 2), end='')
        else:
            print('*', end='')
            print('+' * int((numbers - 1) / 2), end='')
            print('-' * int((numbers - 1) / 2), end='')
        print()

t = int(input())
while t > 0:
    (n, x) = map(int, input().split())
    k = (n + 1 + x) // 2
    if x > n + 1:
        print(-1)
    elif x == n + 1:
        print('+' * n)
    elif x == 1 - n:
        print('-' * n)
    elif x < 1 - n:
        print(-1)
    elif (n + 1) % 2 == x % 2:
        print('+' * (k - 1), end='')
        print('-' * (n + 1 - k))
    elif (n + 1) % 2 != x % 2:
        print('*', end='')
        print('+' * ((n + x) // 2 - 1), end='')
        print('-' * (n - (n + x) // 2))
    t = t - 1
