def quicksort(L, l, r):
    if r - l <= 1:
        return L
    (pivot, lower, upper) = (l, l + 1, l + 1)
    for i in range(l + 1, r):
        if L[i] > L[pivot]:
            upper += 1
        else:
            (L[i], L[lower]) = (L[lower], L[i])
            lower += 1
            upper += 1
    (L[l], L[lower - 1]) = (L[lower - 1], L[l])
    lower = lower - 1
    quicksort(L, l, lower)
    quicksort(L, lower + 1, r)
    return L
