    if dp[m][n] != -1:
        return dp[m][n]
    if m == 0 or n == 0:
        dp[m][n] = 0
        return 0
    if s1[m - 1] == s2[n - 1]:
        dp[m][n] = 1 + lcs(s1, s2, m - 1, n - 1, dp)
        return dp[m][n]
    dp[m - 1][n] = lcs(s1, s2, m - 1, n, dp)
    dp[m][n - 1] = lcs(s1, s2, m, n - 1, dp)
    return max(lcs(s1, s2, m - 1, n, dp), lcs(s1, s2, m, n - 1, dp))
