T = int(input())
for i in range(T):
    line = input().split(' ')
    X = int(line[0])
    B = Y = int(line[1])
    if X == Y:
        print(line[0] + ' ' + line[0] + ' ' + line[0])
    else:
        if B > 0:
            A = 0
        elif B == 0:
            A = -1
        else:
            A = 2 * B
        C = 3 * X - A - B
        while C < B:
            if C <= A:
                A = A - (B - C)
            else:
                A = A + (B - C)
            C = 3 * X - A - B
        print(str(A) + ' ' + str(B) + ' ' + str(C))

