    t = []
    for i in range(1, n, 2):
        m.append(abs(arr[i - 1]))
        t.append(abs(arr[i]))
    if n % 2 != 0:
        m.append(abs(arr[n - 1]))
    k = 1
    while k > 0:
        h = max(t)
        l = min(m)
        if l >= h:
            break
        m[m.index(l)] = h
        t[t.index(h)] = l
        k -= 1
    print(sum(m) - sum(t))
