def solve(arr, n):
    p = []
    m = []
    for i in range(n):
        if i % 2:
            m.append(arr[i])
        else:
            p.append(arr[i])
    if max(m) > min(p):
        return sum(p) - min(p) + max(m) - (sum(m) - max(m) + min(p))
    else:
        return sum(p) - sum(m)
