def a_pwr_n(a, n, mod):
    if n == 1:
        return a % mod
    if n % 2 == 0:
        val = a_pwr_n(a, n // 2, mod)
        return val % mod * val % mod
    else:
        return a % mod * a_pwr_n(a, n - 1, mod) % mod

