def busqueda_binaria(nums, x):
    izq = 0
    der = len(nums) - 1
    while izq <= der:
        medio = int((izq + der) / 2)
        if nums[medio] == x:
            return medio
        elif nums[medio] > x:
            der = medio - 1
        else:
            izq = medio + 1
    return -1

