    while len(a) > 1:
        if a[0] == a[-1]:
            del a[0]
            del a[-1]
        else:
            if a[0] > a[-1]:
                a[0] -= a[-1]
                del a[-1]
            else:
                a[-1] -= a[0]
                del a[0]
            count += 1
    print(count)
