for _ in range(int(input())):
    a = int(input())
    x = str(input())
    p = []
    n = 0
    if x.count('0') == 0 or x.count('1') == 0:
        p.append(-1)
    else:
        for i in range(len(x)):
            if x[i] == '1':
                p.append(i + 1)
            if x.count('1') == len(p) and len(p) == i + 1 and (i + 1 < a):
                for j in range(i + 2, a + 1):
                    p.append(j)
            if len(p) == a:
                break
            elif len(p) == x.count('1'):
                k = a * 2
                for j in range(k - (a - len(p)) + 1, k + 1):
                    p.append(j)
    for i in p:
        print(i, end=' ')
    print()

