from collections import deque

def getCharge(s: list, node: int) -> int:
    chargeSum = 0
    if node > 0:
        chargeSum += 1 if s[node - 1] == '+' else -1 if s[node - 1] == '-' else 0
    if node < len(s) - 1:
        chargeSum += 1 if s[node + 1] == '+' else -1 if s[node + 1] == '-' else 0
    return chargeSum

def solve(s: list):
    queue = deque()
    for i in range(len(s)):
        if s[i] == '0':
            queue.append(i)
    while len(queue) > 0:
        size = len(queue)
        updates = []
        for i in range(size):
            node = queue.popleft()
            charge = getCharge(s, node)
            if charge > 0:
                updates.append((node, '+'))
            elif charge < 0:
                updates.append((node, '-'))
        for element in updates:
            s[element[0]] = element[1]
            if element[0] > 0 and s[element[0] - 1] == '0':
                queue.append(element[0] - 1)
            if element[0] < len(s) - 1 and s[element[0] + 1] == '0':
                queue.append(element[0] + 1)
    print(s.count('0'))
