t = int(input())
for _ in range(t):
    (x, y) = map(int, input().split())
    for i in range(1, int(y ** (1 / 2)) + 1):
        if y % i == 0:
            z = i
    c = y // z
    v = min(c, z)
    f = max(c, z)
    if x < v:
        print(0, x)
        print(min(c, z), max(c, z))
    elif x > f:
        b = x - f - 1
        m = x - v + 1
        if b > f:
            print(min(b, abs(x - b)), max(b, abs(x - b)))
            print(min(c, z), max(c, z))
        elif m < v:
            print(min(m, abs(x - m)), max(m, abs(x - m)))
            print(min(c, z), max(c, z))
        else:
            print(-1)
    else:
        b = x - v + 1
        if b < v:
            print(min(b, abs(x - b)), max(b, abs(x - b)))
            print(min(c, z), max(c, z))
        else:
            print(-1)
