            for i in range(N):
                temp_sum += A[i]
                if temp_sum == summer / 2:
                    counter += 1
            print(counter)
        else:
            arr1 = A + A
            counter = 0
            temp_sum = 0
            for i in range(2 * N):
                temp_sum += arr1[i]
                if temp_sum == summer:
                    counter += 1
            print(counter)

