for _ in range(int(input())):
    (n, m) = (int(i) for i in input().split())
    a = input()
    if a.count('0') == n:
        print(n * m)
        continue
    if m % 2 == 0:
        a = a + a
    s = list(map(int, list(a)))
    pre = [0] * len(s)
    pre[0] = s[0]
    for i in range(1, len(s)):
        pre[i] = pre[i - 1] + s[i]
    sm = sum(s)
    cnt = 0
    for i in range(len(s)):
        if pre[i] == sm - pre[i]:
            cnt += 1
    print(cnt)

for i in range(int(input())):
