for _ in range(int(input())):
    (n, m) = map(int, input().split(' '))
    a = input()
    s = []
    t = 0
    for i in range(n):
        t += int(a[i])
        s.append(t)
    res = 0
    if t == 0:
        print(n * m)
    elif m * t % 2 == 1:
        print(0)
    else:
        for j in range(m):
            pref = j * t
            suf = (m - j - 1) * t
            if abs(suf - pref) <= t:
                for i in range(n):
                    if pref + int(s[i]) == suf + t - int(s[i]):
                        res += 1
        print(res)

