def solve(n: int, m: int, s: str) -> int:
    on = s.count('1')
    ze = s.count('0')
    ans = 1
    if on == 0:
        ans = m * n
    elif on * m % 2 == 1:
        return 0
    elif on % 2 == 1 or m % 2 == 0:
        for i in range(len(s)):
            if s[i] == '0':
                ans += 1
            else:
                break
        for i in range(len(s) - 1, -1, -1):
            if s[i] == '0':
                ans += 1
            else:
                break
    elif m % 2 == 1:
        k = on // 2
        i = 0
        while k:
            if s[i] == '1':
                k -= 1
            i += 1
        while i < len(s):
            if s[i] == '0':
                ans += 1
            else:
                break
            i += 1
    return ans
