for i in range(T):
    (N, M) = [int(z) for z in input().strip().split()]
    A = [int(z) for z in input()]
    summer = sum(A)
    if summer == 0:
        print(N * M)
    else:
        tot_sum = summer * M
        if tot_sum % 2 == 1:
            print(0)
        elif M % 2 == 1:
            counter = 0
            temp_sum = 0
            for i in range(N):
                temp_sum += A[i]
                if temp_sum == summer / 2:
                    counter += 1
            print(counter)
        else:
            arr1 = A + A
            counter = 0
            temp_sum = 0
            for i in range(2 * N):
                temp_sum += arr1[i]
                if temp_sum == summer:
                    counter += 1
            print(counter)

for i in range(int(input())):
    (n, m) = map(int, input().split())
    s = input()
    c_1 = s.count('1')
    c = 0
    if c_1 == 0:
        print(n * m)
    elif c_1 * m % 2 == 1:
        print(0)
    elif m % 2 == 0:
        for i in range(n):
            if s[i] == '0':
                c = c + 1
            else:
                break
        for i in reversed(range(n)):
            if s[i] == '0':
                c = c + 1
            else:
                break
        print(c + 1)
    else:
        summ = 0
        for i in range(n):
            if s[i] == '1':
                summ = summ + 1
            if summ == int(c_1 / 2):
                c = c + 1
        print(c)

for _ in range(int(input())):
    (n, m) = map(int, input().split(' '))
    a = input()
    s = []
    t = 0
    for i in range(n):
        t += int(a[i])
        s.append(t)
    res = 0
    if t == 0:
        print(n * m)
    elif m * t % 2 == 1:
        print(0)
    else:
        for j in range(m):
            pref = j * t
            suf = (m - j - 1) * t
            if abs(suf - pref) <= t:
                for i in range(n):
                    if pref + int(s[i]) == suf + t - int(s[i]):
                        res += 1
        print(res)

