    ind = n - 1
    memo = [0] * n
    while ind >= 0:
        if a[ind] < b[ind]:
            memo[ind] = 1
        elif a[ind] > b[ind]:
            memo[ind] = 0
        elif ind == n - 1:
            memo[ind] = 0
        else:
            memo[ind] = memo[ind + 1]
        ind -= 1
    return sum(memo)
