def solve(S, A, m, n):
    S = list(S)
    (i, j) = (0, 0)
    while i < m and j < n:
        if S[i] == A[j]:
            j += 1
        elif S[i] == '?':
            if A[j] != 'a':
                S[i] = 'a'
            else:
                S[i] = 'e'
        i += 1
    if j < n:
        return ''.join(S)
    return -1
t = int(input())
for i in range(t):
    (m, n) = map(int, input().split())
    S = input()
    A = input()
    print(solve(S, A, m, n))

