def solve(n, a, b):
    dp = [[None] * 54 for _ in range(n)]
    a = [ord(a[i]) for i in range(n)]
    b = [ord(b[i]) for i in range(n)]

    def go(i, diff):
        if i == n:
            return abs(diff)
        if dp[i][diff + 26] is not None:
            return dp[i][diff + 26]
        inc = 26 - (a[i] - b[i]) if a[i] > b[i] else b[i] - a[i]
        dec = 26 - (b[i] - a[i]) if b[i] > a[i] else a[i] - b[i]
        if a[i] == b[i]:
            dp[i][diff + 26] = go(i + 1, diff)
        elif diff < 0:
            if diff - dec >= -26:
                dp[i][diff + 26] = min(go(i + 1, diff + inc), go(i + 1, diff - dec))
            else:
                dp[i][diff + 26] = go(i + 1, diff + inc)
        elif diff > 0:
            if diff + inc <= 26:
                dp[i][diff + 26] = min(go(i + 1, diff + inc), go(i + 1, diff - dec))
            else:
                dp[i][diff + 26] = go(i + 1, diff - dec)
        else:
            dp[i][diff + 26] = min(go(i + 1, diff + inc), go(i + 1, diff - dec))
        return dp[i][diff + 26]
    return go(0, 0)
