t = int(input())
for _ in range(t):
    n = int(input())
    b = list(map(int, input().split()))
    if n % 2:
        xor = 0
        for el in b:
            xor = xor ^ el
    else:
        xor = min(b)
    (a, f) = ([], False)
    for el in b:
        if el != xor or f:
            a += [~xor & el | ~el & xor]
        else:
            f = True
    print(' '.join(map(str, a)))

t = int(input())
for _ in range(t):
    n = int(input())
    arr = list(map(int, input().split()))
    if n % 2 != 0:
        tm = 0
        for ele in arr:
            tm = tm ^ ele
        ans = []
        for ele in arr:
            if ele != tm:
                ans.append(ele ^ tm)
        while len(ans) < n:
            ans.append(0)
        for ele in ans:
            print(ele, end=' ')
        print()
    else:
        tm = 0
        arr.sort()
        if 1 == 1:
            for i in range(1, n + 1):
                print(arr[i] ^ arr[0], end=' ')
            print()
