def GetPrimeFactors(num):
    even = 0
    odd = 0
    while num % 2 == 0:
        even += 1
        num = num // 2
    for i in range(3, int(math.sqrt(num)) + 1, 2):
        while num % i == 0:
            if i % 2 == 0:
                even += 1
            else:
                odd += 1
            num = num // i
    if num > 2:
        if num % 2 == 0:
            even += 1
        else:
            odd += 1
    return (even, odd)
