def generatePrimeFactors(n):
    k = n
    e = 0
    o = 0
    while n % 2 == 0:
        e = e + 1
        n = n // 2
    for i in range(3, int(k ** 0.5) + 1, 2):
        while n % i == 0 and n > 1:
            o = o + 1
            n = n // i
        if n == 1:
            break
    if n != 1:
        o = o + 1
    return [e, o]
