    while i * i <= n:
        while n % i == 0:
            n //= i
            if i % 2 != 0:
                odd_count += 1
            else:
                even_count += 1
        i += 1
    if n > 1:
        if n % 2 != 0:
            odd_count += 1
        else:
            even_count += 1
    if a >= 0 and b >= 0:
        return a * even_count + b * odd_count
