        if even_count > 0:
            return a * even_count
        elif odd_count > 0:
            return b
    elif a < 0 and b >= 0:
        res = b * odd_count
        if even_count > 0:
            return res + a
        return res
    elif a < 0 and b < 0:
        if even_count > 0:
            return a
        elif odd_count > 0:
            return b
