    d = {n: []}
    for j in range(1, n):
        d[j] = [j + 1]
        print(j, j + 1)
        m -= 1
    x = 3
    while m > 0:
        for j in range(1, x):
            if d[j][-1] < x:
                d[j].append(x)
                print(j, x)
                m -= 1
                if m == 0:
                    break
        x += 1
