def knapsack01(wt, val, w, n, dp):
    if n == 0 or w == 0:
        return 0
    if dp[n][w] != -1:
        return dp[n][w]
    if wt[n - 1] > w:
        ans = knapsack01(wt, val, w, n - 1, dp)
        dp[n][w] = ans
        return ans
    else:
        ans = max(val[n - 1] + knapsack01(wt, val, w - wt[n - 1], n - 1, dp), knapsack01(wt, val, w, n - 1, dp))
        dp[n][w] = ans
        return ans

def knapSack(w, wt, val, n):
    dp = [[-1] * (w + 1) for i in range(0, n + 1)]
    return knapsack01(wt, val, w, n, dp)
