def yn(b, caps=False):
    if b:
        returning = 'Yes'
    else:
        returning = 'No'
    if caps:
        return returning.upper()
    return returning

def isPrime(n):
    if n > 1:
        for i in range(2, int(n ** 0.5) + 1):
            if not n % i:
                return False
        else:
            return True
    return True

