class mutableStr(UserString):

    def ins(self, n, s):
        if n < 0:
            self.data = self.data = ''.join(list(self.data)[:n + 1]) + s + ''.join(list(self.data)[n + 1:])
        else:
            self.data = ''.join(list(self.data)[:n]) + s + ''.join(list(self.data)[n:])

    def rem(self, s):
        self.data = self.data.replace(s, '')
