from collections import Counter, defaultdict

class Solution:

    def fact(self, n, dp):
        if n == 1:
            return 1
        if dp[n]:
            return dp[n]
        dp[n] = n * self.fact(n - 1, dp)
        return dp[n]

    def splitingBalls(self, n, bolls, dp):
        ans = 0
        freq = Counter(bolls)
        for (bol, val) in freq.items():
            ans += val * self.fact(bol, dp)
        return ans % (10 ** 9 + 7)

