    if s[0] == '0':
        for i in range(len(s)):
            if s[i] == '1':
                if i + 1 < len(s) and s[i + 1] == '0':
                    special_index = i
                break
    p1 = 1
    p2 = None
    for i in range(len(s) - 1, -1, -1):
        v_right = set()
        v_left = set()
        if i == special_index:
            if 3 - p2 == 1:
                ones += 1
            elif 3 - p2 == 2:
                twos += 1
        if s[i] == '0' and i < len(s) - 1 and (s[i + 1] == '0') and (i > 1) and (s[i - 1] == '1') and (s[i - 2] == '1'):
