        if s[i] == '0' and i < len(s) - 1 and (s[i + 1] == '1') and (i + 1 != special_index):
            if p2 == 1:
                v_right.add(1)
            elif p2 == 2:
                v_right.add(2)
        if s[i] == '0' and i > 0 and (s[i - 1] == '1') and (i - 1 != special_index):
            if 3 - p1 == 1:
                v_left.add(1)
            elif 3 - p1 == 2:
                v_left.add(2)
        vs = v_right | v_left
        if len(vs) == 2:
            doubles += 1
        elif 1 in vs:
            ones += 1
        elif 2 in vs:
            twos += 1
        p1 = p1 * 2 % 3
        if p2 is None:
            p2 = 1
        else:
            p2 = p2 * 2 % 3
    if val == 1 and twos + doubles > 0 or (val == 2 and ones + doubles > 0):
        return 1
    if val == 2 and twos + doubles > 1 or (val == 1 and ones + doubles > 1):
        return 2
    if threes > 0:
        return 3
    return -1
