def build(tree, arr, start, end, index):
    if start == end:
        if start % 2 == 0:
            tree[index] = arr[start]
        else:
            tree[index] = (-arr[start] + 26) % 26
        return
    mid = start + (end - start) // 2
    build(tree, arr, start, mid, 2 * index + 1)
    build(tree, arr, mid + 1, end, 2 * index + 2)
    tree[index] = (tree[2 * index + 1] + tree[2 * index + 2] + 26) % 26

def update(tree, index, start, end, pos, val):
    if start == end:
        if start % 2 == 0:
            tree[index] = val
        else:
            tree[index] = -val
        return
    mid = start + (end - start) // 2
    if mid >= pos:
        update(tree, 2 * index + 1, start, mid, pos, val)
    else:
        update(tree, 2 * index + 2, mid + 1, end, pos, val)
    tree[index] = tree[2 * index + 1] + tree[2 * index + 2]

def FindSum(tree, start, end, L, R, index):
    if L > end or R < start:
        return 0
    if L <= start and R >= end:
        return (tree[index] + 26) % 26
    mid = start + (end - start) // 2
    X = (FindSum(tree, start, mid, L, R, 2 * index + 1) + 26) % 26
    Y = (FindSum(tree, mid + 1, end, L, R, 2 * index + 2) + 26) % 26
    return (X + Y) % 26
