            W -= 2
            B -= 1
        st = st + 'B' * B + 'W' * W
        print(st)
        print(1, 2)
        print(2, 3)
        for end in range(4, 4 + B):
            print(2, end)
        for end in range(4 + B, 4 + W + B):
            print(2, end)
