def check(n, m, k):
    x = 0
    while x <= n and x * x <= k:
        rem = k - x * m
        if rem < 0:
            break
        if x == n:
            if rem == 0:
                return True
        else:
            y = rem // (n - x)
            if y <= m and y * (n - x) == rem:
                return True
        x += 1
    return False
