def calculate(p, q):
    mod = 998244353
    expo = 0
    expo = mod - 2
    while expo:
        if expo & 1:
            p = p * q % mod
        q = q * q % mod
        expo >>= 1
    return p

def count_factors(n):
    i = 1
    c = 0
    ans = []
    while i <= math.sqrt(n):
        if n % i == 0:
            if n // i == i:
                c += 1
                ans.append(n // i)
            else:
                c += 2
                ans.append(i)
                ans.append(n // i)
