def twosum(a, n, x):
    rem = []
    for i in range(x):
        rem.append(0)
    for i in range(n):
        if a[i] < x:
            rem[a[i] % x] += 1
    for i in range(1, x // 2):
        if rem[i] > 0 and rem[x - i] > 0:
            return True
    if i >= x // 2:
        if x % 2 == 0:
            if rem[x // 2] > 1:
                return True
            else:
                return False
        elif rem[x // 2] > 0 and rem[x - x // 2] > 0:
            return True
        else:
            return False

def divSum(num):
    result = 0
    i = 2
    while i <= math.sqrt(num):
        if num % i == 0:
            if i == num / i:
                result = result + i
            else:
                result = result + (i + num / i)
        i = i + 1
    return result + 1 + num

