def ans(a, b, i):
    if len(a) == 0:
        return 0
    x = [[], []]
    for j in a:
        if j % b[i] == 0:
            x[0].append(j)
        else:
            x[1].append(j)
    if i == len(b) - 1:
        if i % 2 == 0:
            return min(sum(x[0]), sum(x[1]))
        else:
            return max(sum(x[0]), sum(x[1]))
    if i % 2 == 0:
        return min(ans(x[0], b, i + 1), ans(x[1], b, i + 1))
    else:
        return max(ans(x[0], b, i + 1), ans(x[1], b, i + 1))
